package Mine2024.searchSuggestionsSystem;

import java.util.ArrayList;
import java.util.Arrays;
import java.util.List;

public class Solution2
{
    public static void main(String[] args)
    {
//        String[] products = {"mobile", "mouse", "moneypot", "monitor", "mousepad"};
//        String[] products = {"havana"};
        String[] products = {"","","",""};
//        String searchWord = "mouse";
//        String searchWord = "tatiana";
        String searchWord = "";
        Solution2 s = new Solution2();
        List<List<String>> result = s.suggestedProducts(products, searchWord);
        System.out.println(result);
    }
    public List<List<String>> suggestedProducts(String[] products, String searchWord)
    {
        Arrays.sort(products);
        char[] searchWordArray = searchWord.toCharArray();
        List<List<String>> result = new ArrayList<>();

        int left = 0;
        int right = products.length-1;
        for( int i=0; i<searchWordArray.length; i++ ){
            List<String> partialResult = new ArrayList<>();
            char currentLetter = searchWordArray[i];
            while(left<=right && (products[left].length()<searchWordArray.length || products[left].toCharArray()[i]!=currentLetter)){
                left++;
            }

            while (left<=right && (products[right].length()<searchWordArray.length || products[right].toCharArray()[i]!=currentLetter)){
                right--;
            }

            int remained= right - left+1;
            for(int j=0; j<Math.min(remained,3); j++){
                partialResult.add(products[left+j]);
            }

            result.add(partialResult);
        }




        return result;

    }
}
